import React, { useState } from 'react';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [responses, setResponses] = useState([]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (message.trim()) {
      setResponses((prev) => [...prev, { sender: 'user', text: message }]);
      setMessage('');
      try {
        const response = await fetch('http://127.0.0.1:5000/chat', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ message }),
        });
        const data = await response.json();
        setResponses((prev) => [...prev, { sender: 'bot', text: data.response }]);
      } catch (error) {
        setResponses((prev) => [...prev, { sender: 'bot', text: 'Maaf, terjadi kesalahan.' }]);
      }
    }
  };

  return (
    <div>
      <div className="chat-window">
        {responses.map((res, idx) => (
          <div key={idx} className={res.sender === 'user' ? 'user-message' : 'bot-message'}>
            {res.text}
          </div>
        ))}
      </div>
      <form onSubmit={handleSendMessage}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Tulis pesan..."
        />
        <button type="submit">Kirim</button>
      </form>
    </div>
  );
};

export default Chat;
